    <AccordionWrapper>
      {data.map((item, index) => (
        <AccordionItem key={index}>
          <AccordionHeader onClick={() => handleAccordionClick(index)}>
            {item.title}
          </AccordionHeader>
          {activeIndex === index && (
            <AccordionContent>{item.content}</AccordionContent>
